(8,	1,	'2023-11-12 20:24:52',	'2023-11-12 20:24:52',	'<!-- wp:heading {\"textAlign\":\"center\",\"level\":1,\"style\":{\"typography\":{\"fontStyle\":\"normal\",\"fontWeight\":\"700\"}}} -->\n<h1 class=\"wp-block-heading has-text-align-center\" style=\"font-style:normal;font-weight:700\">BOISSON ÉNERGISANTE 100% NATURELLE</h1>\n<!-- /wp:heading -->\n\n<!-- wp:image {\"align\":\"center\",\"id\":43,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image aligncenter size-full\"><img src=\"http://planty-bintou.local/wp-content/uploads/2023/11/planty-img.png\" alt=\"\" class=\"wp-image-43\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading {\"textAlign\":\"center\",\"level\":6,\"style\":{\"typography\":{\"lineHeight\":\"3\",\"fontStyle\":\"normal\",\"fontWeight\":\"400\"},\"spacing\":{\"padding\":{\"top\":\"0\",\"bottom\":\"0\"}}},\"className\":\"energie\"} -->\n<h6 class=\"wp-block-heading has-text-align-center energie\" style=\"padding-top:0;padding-bottom:0;font-style:normal;font-weight:400;line-height:3\">L’énergie des plantes</h6>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\",\"style\":{\"spacing\":{\"padding\":{\"top\":\"10.06rem\",\"bottom\":\"0\",\"left\":\"0\",\"right\":\"0\"}}},\"className\":\"home-paragraph\"} -->\n<p class=\"has-text-align-center home-paragraph\" style=\"padding-top:10.06rem;padding-right:0;padding-bottom:0;padding-left:0\">Planty, c’est la première boisson<br>énergisante composée à 100 % de produits naturels.<br>Avec son goût frais et fruité, retrouvez votre<br>énergie grâce aux plantes riches en vitamine B2,<br>à n’importe quel moment de la journée.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"textAlign\":\"center\",\"level\":4,\"style\":{\"typography\":{\"textTransform\":\"uppercase\"}},\"className\":\"titre-pink\"} -->\n<h4 class=\"wp-block-heading has-text-align-center titre-pink\" style=\"text-transform:uppercase\">LES Goûts</h4>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">Nous avons une saveur pour chaque moment de votre journée.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:gallery {\"columns\":2,\"linkTo\":\"none\",\"className\":\"gallery-wrapper\"} -->\n<figure class=\"wp-block-gallery has-nested-images columns-2 is-cropped gallery-wrapper\"><!-- wp:image {\"id\":60,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"http://planty-bintou.local/wp-content/uploads/2023/11/estudio-bloom.jpg\" alt=\"\" class=\"wp-image-60\"/><figcaption class=\"wp-element-caption\"><strong>FRAISE</strong></figcaption></figure>\n<!-- /wp:image -->\n\n<!-- wp:image {\"id\":61,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"http://planty-bintou.local/wp-content/uploads/2023/11/estudio-bloom-ezqnxsq.jpg\" alt=\"\" class=\"wp-image-61\"/><figcaption class=\"wp-element-caption\"><strong>PAMPLE MOUSSE</strong></figcaption></figure>\n<!-- /wp:image -->\n\n<!-- wp:image {\"id\":63,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"http://planty-bintou.local/wp-content/uploads/2023/11/jeremy-bezanger.jpg\" alt=\"\" class=\"wp-image-63\"/><figcaption class=\"wp-element-caption\"><strong>FRAM BOISE</strong></figcaption></figure>\n<!-- /wp:image -->\n\n<!-- wp:image {\"id\":62,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"http://planty-bintou.local/wp-content/uploads/2023/11/rodion-kutsaev.jpg\" alt=\"\" class=\"wp-image-62\"/><figcaption class=\"wp-element-caption\"><strong>CITRON</strong></figcaption></figure>\n<!-- /wp:image --></figure>\n<!-- /wp:gallery -->\n\n<!-- wp:buttons {\"className\":\"command-btn\",\"layout\":{\"type\":\"flex\",\"justifyContent\":\"center\"}} -->\n<div class=\"wp-block-buttons command-btn\"><!-- wp:button {\"textAlign\":\"center\"} -->\n<div class=\"wp-block-button\"><a class=\"wp-block-button__link has-text-align-center wp-element-button\" href=\"http://planty-bintou.local/commander/\">Commander</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:buttons -->\n\n<!-- wp:heading {\"textAlign\":\"center\",\"align\":\"wide\",\"style\":{\"spacing\":{\"margin\":{\"top\":\"9.4rem\",\"bottom\":\"3rem\",\"left\":\"0\",\"right\":\"0\"}}},\"className\":\"pense\"} -->\n<h2 class=\"wp-block-heading alignwide has-text-align-center pense\" style=\"margin-top:9.4rem;margin-right:0;margin-bottom:3rem;margin-left:0\">CE QU’ILS EN PENSENT</h2>\n<!-- /wp:heading -->\n\n<!-- wp:columns {\"style\":{\"spacing\":{\"padding\":{\"top\":\"var:preset|spacing|80\",\"bottom\":\"0\",\"left\":\"var:preset|spacing|80\",\"right\":\"var:preset|spacing|80\"},\"margin\":{\"top\":\"0\",\"bottom\":\"0\"},\"blockGap\":{\"top\":\"0\",\"left\":\"0\"}}},\"className\":\"photo3-home\"} -->\n<div class=\"wp-block-columns photo3-home\" style=\"margin-top:0;margin-bottom:0;padding-top:var(--wp--preset--spacing--80);padding-right:var(--wp--preset--spacing--80);padding-bottom:0;padding-left:var(--wp--preset--spacing--80)\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:media-text {\"mediaId\":70,\"mediaLink\":\"http://planty-bintou.local/home/fathia/\",\"mediaType\":\"image\",\"mediaWidth\":35,\"className\":\"fatiha\"} -->\n<div class=\"wp-block-media-text is-stacked-on-mobile fatiha\" style=\"grid-template-columns:35% auto\"><figure class=\"wp-block-media-text__media\"><img src=\"http://planty-bintou.local/wp-content/uploads/2023/11/fathia.png\" alt=\"\" class=\"wp-image-70 size-full\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:heading {\"level\":5} -->\n<h5 class=\"wp-block-heading\">Fatiha</h5>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>La meilleure boisson énergisante disponible sur le marché !</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:media-text --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"class